// Get the input data from the PII Censor node
const inputData = $input.first().json;

// Extract original and censored data
const original = {
  patientId: inputData.patientId,
  fullName: inputData.fullName,
  ssn: inputData.ssn,
  dateOfBirth: inputData.dateOfBirth,
  address: inputData.address,
  phone: inputData.phone,
  email: inputData.email,
  insuranceNumber: inputData.insuranceNumber,
  medicalRecordNumber: inputData.medicalRecordNumber,
  diagnosis: inputData.diagnosis,
  medications: inputData.medications,
  labResults: inputData.labResults,
  visitDate: inputData.visitDate,
  provider: inputData.provider,
  department: inputData.department
};

const censored = inputData.censored;

// Create research dataset with proper anonymization
const researchData = {
  studyId: `STUDY-${original.patientId}`,
  demographics: {
    age: new Date().getFullYear() - new Date(original.dateOfBirth).getFullYear(),
    gender: 'Not Specified', // Remove gender for additional privacy
    region: 'United States'
  },
  medicalData: {
    diagnosis: censored.diagnosis,
    medications: censored.medications,
    labResults: censored.labResults,
    visitDate: censored.visitDate,
    department: censored.department
  },
  providerData: {
    provider: censored.provider,
    department: censored.department
  },
  anonymizedIdentifiers: {
    patientId: censored.patientId,
    medicalRecordNumber: censored.medicalRecordNumber,
    insuranceNumber: censored.insuranceNumber
  },
  researchMetadata: {
    anonymizationDate: new Date().toISOString(),
    studyPurpose: 'Medical Research',
    dataRetention: '7 years',
    irbApproval: 'IRB-2024-001',
    hipaaCompliance: 'De-identified per 45 CFR 164.514',
    piiProcessed: $input.first().json._metadata
  }
};

return [{
  json: researchData
}];
