// Get the input data from the PII Censor node
const inputData = $input.first().json;

// Extract original and censored data
const original = {
  customerId: inputData.customerId,
  fullName: inputData.fullName,
  email: inputData.email,
  phone: inputData.phone,
  passport: inputData.passport,
  nationalId: inputData.nationalId,
  address: inputData.address,
  dateOfBirth: inputData.dateOfBirth,
  ipAddress: inputData.ipAddress,
  lastLogin: inputData.lastLogin,
  dataProcessingConsent: inputData.dataProcessingConsent,
  marketingConsent: inputData.marketingConsent
};

const censored = inputData.censored;

// Create export-ready data structure
const exportData = {
  customerId: original.customerId,
  personalData: {
    name: censored.fullName,
    email: censored.email,
    phone: censored.phone,
    address: censored.address,
    dateOfBirth: censored.dateOfBirth
  },
  identifiers: {
    passport: censored.passport,
    nationalId: censored.nationalId
  },
  technicalData: {
    ipAddress: censored.ipAddress,
    lastLogin: censored.lastLogin
  },
  consent: {
    dataProcessing: original.dataProcessingConsent,
    marketing: original.marketingConsent
  },
  gdprMetadata: {
    exportDate: new Date().toISOString(),
    dataController: "Your Company Name",
    legalBasis: "Consent",
    retentionPeriod: "7 years",
    piiProcessed: inputData._metadata
  }
};

return [{
  json: exportData
}];
