const inputData = $input.first().json;

// Extract original and censored data
const original = {
  customerId: inputData.customerId,
  fullName: inputData.fullName,
  ssn: inputData.ssn,
  driverLicense: inputData.driverLicense,
  email: inputData.email,
  phone: inputData.phone,
  address: inputData.address,
  bankAccount: inputData.bankAccount,
  creditCard: inputData.creditCard,
  applicationDate: inputData.applicationDate,
  status: inputData.status
};

const censored = inputData.censored;

// Create a comprehensive comparison
return [{
  json: {
    summary: `Customer ${original.customerId} processed with preserveStructure=true`,
    original: {
      name: original.fullName,
      ssn: original.ssn,
      email: original.email,
      phone: original.phone,
      bankAccount: original.bankAccount,
      creditCard: original.creditCard
    },
    censored: {
      name: censored.fullName,
      ssn: censored.ssn,
      email: censored.email,
      phone: censored.phone,
      bankAccount: censored.bankAccount,
      creditCard: censored.creditCard
    },
    comparison: {
      fieldsProcessed: Object.keys(original).length,
      fieldsWithPII: Object.keys(original).filter(key => original[key] !== censored[key]).length,
      censoringCharacter: "‚ñà"
    },
    status: original.status,
